## Utility function to avoid some warnings about missing env. variables
sub Env_or {
    my($var,$default) = @_;
    if (exists $ENV{$var}) {
	return $ENV{$var} . ',';
    } else {
	$default = '0' unless $default;
	return "$default" . ',';
    }
}

